import { useState, useEffect } from "react";
import './Bid.css'

function Bid() {


    const [bid, setBid] = useState(0);
    const [timer, setTimer] = useState(20);
    const [highestBidder, setHighestBidder] = useState("no bid yet")
    const [isBiddingOpen, setIsBiddingOpen] = useState(false);
    const [text, setText] = useState('');

    const user1 = "tekital1000@gmail.com";
    const user2 = "fabian.kjellberg98@gmail.com";

    const startBid = () => {
        setIsBiddingOpen(true);
    };


    useEffect(() => {
        let interval;

        if (isBiddingOpen && timer > 0) {
            interval = setInterval(() => {
                setTimer((prevTimer) => prevTimer - 1);
            }, 1000);
        }

        if (timer === 0) finishTransaction()

        return () => {
            clearInterval(interval);
        };
    }, [isBiddingOpen, timer]);


    const finishTransaction = () => {
        setText(highestBidder, " won the auction Congratz!!")
    }

    const doBid = (user) => {

        setBid(bid + 50)
        setHighestBidder(user)
    }

    return (
        <>
            <div className="bid">
                <button onClick={startBid}>
                    start bid
                </button>
                <hr></hr>
                <p>
                    {user1} makes an offer
                </p>

                <button onClick={() => doBid(user1)}>
                    bid +50€
                </button>
                <hr></hr>
                <p>
                    {user2} makes an offer
                </p>
                <button onClick={() => doBid(user2)}>
                    bid +50€
                </button>

                <hr></hr>

                <p>time left: {timer}</p>
                <p>current bid:  {bid}</p>
                <p>highest Bidder: {highestBidder}</p>
                <p>{text}</p>
            </div>

        </>
    )
}export default Bid;
