import './ConfirmUser.css';
import { useState , useEffect } from 'react';
import { useNavigate , useLocation } from 'react-router-dom';
import { CognitoUser, AuthenticationDetails,} from 'amazon-cognito-identity-js';
import UserPool from '../../utilities/UserPool';

function ConfirmUser() {


    const location = useLocation();
    const navigate = useNavigate();

    const [cognitoUser, setCognitoUser] = useState(null);

    const confirmEmail = new URLSearchParams(location.search).get('userid');

    useEffect(() => {
        if (confirmEmail === null) {
            navigate('/');
        }
        const userData = {
            Username: confirmEmail,
            Pool: UserPool,
        };

        setCognitoUser(new CognitoUser(userData));
    }, []);

    const [formData, setFormData] = useState({
        code:''
     });

    const handleChange = ((e) => {
        const { name, value } = e.target;
        const numericValue = value.replace(/\D/g, '').substring(0, 6)
        setFormData({ ...formData, [name]: numericValue });
    });

    const handleError = ((error) => {
        console.log("Error object: ", error.message);
        if(error.message === "User is not confirmed."){
            navigate(`/userconfirmation?userid=${formData.email}`)
        }
    });

  const handleSubmit = ((event) =>{
        event.preventDefault();

        cognitoUser.confirmRegistration(formData.code, true, (err, result) => {
            if (err) {
                console.error("Error confirming user account:", err);
            } else {
                console.log("User account confirmed successfully:", result);
            }
        });
    });

    const resendMail = ((event) => {
        event.preventDefault();

        cognitoUser.resendConfirmationCode((err, result) => {
            if (err) {
                console.error("Error resending confirmation code:", err);
                // Handle the error, show a message, etc.
            } else {
                console.log("Confirmation code resent successfully:", result);
                // Provide feedback to the user, e.g., show a success message
            }
        });       
    });


  return (
    <>
      <div className='confirm'>
        <div className='confirm-window'>
            <div className='confirm-greeting'>
                <h3>Just one more step!</h3>
                <p>Before you can log in you just need to verify your email.</p>
                <p>We have sent an email to <b>{confirmEmail}</b> with a 6 digit code. Please input the code to confirm your Email.</p>
            </div>
            <form className="confirm-form">
                <label className='confirm-labels'>
                <p>Enter the 6 digit code here to confirm</p>
                <input
                    type='text'
                    name="code"
                    value={formData.code}
                    onChange={handleChange}
                    pattern="[0-9]*"
                    maxLength="6"
                />
                </label>
                <div className='confirm-buttons'>
                    <button onClick={handleSubmit}>Confirm Account</button>
                    <button onClick={resendMail}>Resend Email</button>
                </div>
            </form>
        </div>
      </div>
    </>
  );
}

export default ConfirmUser;
