import './SignUp.css';
import { useState } from 'react';
import UserPool from '../../utilities/UserPool.js';

function SignUp() {
  const [formData, setFormData] = useState({
    email:'',
    password:'',
    repeatPassword:''
  });

  const handleChange = ((e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
  });

  const handleSubmit = ((event) => {
      event.preventDefault();

      console.log(formData.password," : ",formData.repeatPassword)

      if(formData.password === formData.repeatPassword)

      UserPool.signup()
  });

  return (
    <>
      <div className='signup'>
        <div className='signup-window'>
          <div className='signup-greeting'>
            <h2>Sign up and start bidding today!</h2>
            <p>ZEROTO60S wants to become an international Auction player in the leisure, retail and adjacent sector.</p>
          </div>
          <form className="signup-form" onSubmit={handleSubmit}>
            <label className='signup-labels'>
              <p>Email</p>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </label>
            <label className='signup-labels'>
              <p>Password</p>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </label>
            <label className='signup-labels'>
              <p>Repeat Password</p>
              <input
                type="password"
                name="repeatPassword"
                value={formData.repeatPassword}
                onChange={handleChange}
                required
              />
            </label>
            <div className='signup-register-button'>
              <button type="submit">Create Account</button>
            </div>
            <div className='signup-login'>
              <a href="login">Already have an account? Click here!</a>
            </div>
            </form>
        </div>
      </div>
    </>
  );
}

export default SignUp;
