                {
                    title: 'johnwick.com',
                    description: 'johnwick.com',
                    type: 'domain_name',
                    inspection_period: 259200,
                    quantity: 1,
                    schedule: [
                        {
                            amount: price,
                            payer_customer: 'me',
                            beneficiary_customer: 'keanu.reaves@test.escrow.com',
                        },
                    ],
                    extra_attributes: {

                    },
                },
            ],
        };

        axios.post(apiEndpoint, requestData, {
            auth: {
                username: email,
                password: apiKey,
            },
            headers: {
                'Content-Type': 'application/json',
            },
            crossdomain: true,
        })
            .then(response => {
                console.log('Escrow API Response:', response.data);
            })
            .catch(error => {
                console.error('Error making API request:', error.message);
            });
    }
}export default TestEscrow
