import { useState, useEffect } from 'react';
import './Carousel.css';

function Carousel(props) {
    const [imageSrc, setImageSrc] = useState(props.imageSrc);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [maxHeight, setMaxHeight] = useState(0);
    const [automatic, setAutomatic] = useState(true);
    const [currentImageHeight, setCurrentImageHeight] = useState(imageSrc[0].height);

    useEffect(() => {
        const heights = imageSrc.map((src) => {
            const img = new Image();
            img.src = src;
            console.log(img.height)

            return img.height;
        });

        const intervalId = setInterval(() => {
            console.log("hej");
            goToNext();
        }, 4500);

        setMaxHeight(Math.max(...heights));

        return () => {
            clearInterval(intervalId); // Clear the interval when the component unmounts or when imageSrc changes
        };
    }, [imageSrc]);

    const goToPrevious = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? imageSrc.length - 1 : prevIndex - 1));
    };

    const goToNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex === imageSrc.length - 1 ? 0 : prevIndex + 1));
    };

    return (
        <>

                <div className='carousel' >
                    <button className='previous' onClick={goToPrevious}>
                        Previous
                    </button>
                    {imageSrc.map((src, index) => (
                        <img
                            key={index}
                            src={src}
                            alt={`image-${index}`}
                            style={{
                                transform: `translateX(${100 * (index - currentIndex)}%)`,
                            }}
                        />
                    ))}
                    <button className='next' onClick={goToNext}>
                        Next
                    </button>
                </div>

        </>
    );
}

export default Carousel;
