import { useState, useEffect } from "react";
import './Transaction.css'
import '../../utilities/TestEscrow.js'
import TestEscrow from "../../utilities/TestEscrow.js";

function Transaction() {

    const [buyerEmail, setBuyerEmail] = useState("example@email.com")

    const [formData, setFormData] = useState({
        buyerEmail: '',
        sellerEmail: '',
        price: '',
        description: '',
        imageURL: '',
        itemName: ''
    });

    const handleChange = ((e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    });

    function makeTransaction(){
        TestEscrow.createOffer(formData);
    }


    return (
        <>
            <div className="transaction-form">
                <form className="transaction-form" onSubmit={makeTransaction}>
                    <label className='transaction-labels'>
                        <p>Buyer Email</p>
                        <input
                            type="email"
                            name="buyerEmail"
                            value={formData.buyerEmail}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label className='transaction-labels'>
                        <p>Seller Email</p>
                        <input
                            type="email"
                            name="sellerEmail"
                            value={formData.sellerEmail}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label className='transaction-labels'>
                        <p>Price</p>
                        <input
                            type="number"
                            name="price"
                            value={formData.price}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label className='transaction-labels'>
                        <p>description</p>
                        <input
                            type="text"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            required
                        />
                    </label>

                    <label className='transaction-labels'>
                        <p>image URL</p>
                        <input
                            type="text"
                            name="imageURL"
                            value={formData.imageURL}
                            onChange={handleChange}
                            required
                        />
                    </label>

                    <label className='transaction-labels'>
                        <p>Item Name</p>
                        <input
                            type="text"
                            name="itemName"
                            value={formData.itemName}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    {/*showError && (<p className="index-error-message">{errorMessage}</p>)*/}
                    <div className='transaction-submit'>
                    <button type="submit">Create Transaction</button>
                    </div>
                </form>
            </div>

        </>
    )
}export default Transaction;
