import './SignUp.css';
import { useState } from 'react';
import UserPool from '../../utilities/UserPool.js';

function SignUp() {

  const [errorMessage, setErrorMessage] = useState("");
  const [showError, setShowError] = useState(false);

  const [formData, setFormData] = useState({
    email:'',
    password:'',
    repeatPassword:''
  });

  const handleChange = ((e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
  });

  const handleSubmit = ((event) => {
      event.preventDefault();



      if(formData.password === formData.repeatPassword && formData !== ""){

        UserPool.signUp(formData.email, formData.password, [], null, (err, data) => {
          if(err){
            setErrorMessage(err.code)
            setShowError(true)

            console.log(err)
          }else{
            console.log(data)
          }
        })
      }else(
        console.log("password not matching")
      )


  });

  return (
    <>
      <div className='signup'>
        <div className='signup-window'>
          <div className='signup-greeting'>
            <h2>Sign up and start bidding today!</h2>
            <p>ZEROTO60S wants to become an international Auction player in the leisure, retail and adjacent sector.</p>
          </div>
          <form className="signup-form" onSubmit={handleSubmit}>
            <label className='signup-labels'>
              <p>Email</p>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </label>
            <label className='signup-labels'>
              <p>Password</p>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </label>
            <label className='signup-labels'>
              <p>Repeat Password</p>
              <input
                type="password"
                name="repeatPassword"
                value={formData.repeatPassword}
                onChange={handleChange}
                required
              />
            </label>
            {showError && (
                <p className="index-errorMessage">{JSON.stringify(errorMessage)}</p>
            )}
            <div className='signup-register-button'>
              <button type="submit">Create Account</button>
            </div>
            <div className='signup-login'>
              <a href="login">Already have an account? Click here!</a>
            </div>
            </form>
        </div>
      </div>
    </>
  );
}

export default SignUp;
