import axios from 'axios';

class TestEscrow {

    static async createOffer(buyer, seller, price){



        const apiEndpoint = 'https://api.escrow-sandbox.com/2017-09-01/transaction';
        const email = 'tekital1000@gmail.com';
        const apiKey = '16851_w2dHAxMU2hwBPMDmIadYwplvcV4RuoGlYdieLAHhRGjK6cxUTqYHAIvxCXTIWRW8';
        const authorizationHeader = `Basic ${btoa(`${email}:${apiKey}`)}`;

        const requestData = {
            parties: [
                {
                    role: 'buyer',
                    customer: buyer,
                },
                {
                    role: 'seller',
                    customer: seller,
                },
            ],
            currency: 'usd',
            description: 'The sale of johnwick.com',
            items: [
                {
                    title: 'johnwick.com',
                    description: 'johnwick.com',
                    type: 'domain_name',
                    inspection_period: 259200,
                    quantity: 1,
                    schedule: [
                        {
                            amount: price,
                            payer_customer: 'me',
                            beneficiary_customer: 'keanu.reaves@test.escrow.com',
                        },
                    ],
                    extra_attributes: {

                    },
                },
            ],
        };

        try {
            const response = await fetch(apiEndpoint, {
                method: 'POST',
                mode: 'no-cors',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': authorizationHeader,
                },
                body: JSON.stringify(requestData),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();

            console.log('Escrow API Response:', data);
        } catch (error) {
            console.error('Error making API request:', error.message);
        }
    }
}export default TestEscrow
