import './HowToBuy.css'
import GenericBanner from '../../components/GenericBanner/GenericBanner.js'

function HowToBuy(){


    return(
        <>
            <GenericBanner bannerText={"HOW TO BUY"}/>  
            <div className='howtobuy'>
                  <h1>HOW IT WORKS</h1>
                  <p>At Zeroto60s, we've created an efficient buying process to ensure simplicity and security for both buyers and sellers.</p>
                  <p><b>Step 1</b> <em>-SIGN UP-</em> Sign up with Zeroto60s account and securely link your payment card. Rest assured, the card only be charged if you emerge as the successful bidder</p> 
                  <p><b>Step 2</b> <em>-SEARCH-</em> Simply use our seach bar to find and browse through the watches that catch your interest. Once you've found the perfect pieces you want to bid on, take your time to read the descriptions and details carefully. You can also ask the seller directly using our chat platform.</p> 
            </div>    
        </>
    )

}
export default HowToBuy;
