import React from "react";
import { useState } from "react";
import axios from 'axios';

class CurrentListingManager {
    constructor(apiUrl) {
        this.apiUrl = apiUrl;
        this.axiosInstance = axios.create({
            baseURL: apiUrl,
            headers: {
                'Content-Type': 'application/json'
            }
        });
    }

    RetrieveListings() {
        // Make an Axios request and assign the promise to returnData
        const returnData = this.axiosInstance.get('/product/listAll')
            .then(response => {
                return response.data; // Return the JSON data from the response
            })
            .catch(error => {
                console.error('Error retrieving listings', error);
                if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.error(error.response.data);
                    console.error(error.response.status);
                    console.error(error.response.headers);
                } else if (error.request) {
                    // The request was made but no response was received
                    console.error(error.request);
                } else {
                    // Something happened in setting up the request that triggered an Error
                    console.error('Error', error.message);
                }
                return []; // Return an empty array or any other fallback data
            });

        // Since Axios requests are asynchronous, returnData will be a promise
        return returnData;
    }
}

export default CurrentListingManager;
